$((function(){const e=document.querySelectorAll(".c-card,.masonry-grid__grid-item");Array.prototype.forEach.call(e,e=>{e.style.cursor="pointer";let t,n,o=e.querySelector("a");e.onmousedown=()=>t=+new Date,e.onmouseup=()=>{n=+new Date,n-t<200&&o.click()}});let t=.01*window.innerHeight;document.documentElement.style.setProperty("--vh",t+"px"),window.addEventListener("resize",()=>{let e=.01*window.innerHeight;document.documentElement.style.setProperty("--vh",e+"px")})}));